mapper.insert:

insert into stock (id, name, stock_exchange,
stock_code, create_time, motify_time
)
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR},
#{stockExchange,jdbcType=VARCHAR},
#{stockCode,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{motifyTime,jdbcType=TIMESTAMP}
)

mapper.delete:
delete from stock
where 1

	and id=#{id,jdbcType=BIGINT}


	and stock_exchange = #{stockExchange,jdbcType=VARCHAR}


	and stock_code = #{stockCode,jdbcType=VARCHAR}


	and create_time = #{createTime,jdbcType=TIMESTAMP}


	and motify_time = #{motifyTime,jdbcType=TIMESTAMP}


	and name = #{name,jdbcType=VARCHAR}

mapper.select:
