	and stock_exchange = #{stockExchange,jdbcType=VARCHAR}


	and stock_code = #{stockCode,jdbcType=VARCHAR}


	and create_time = #{createTime,jdbcType=TIMESTAMP}


	and motify_time = #{motifyTime,jdbcType=TIMESTAMP}


	and name = #{name,jdbcType=VARCHAR}

select
count(*)
from stock
where 1

	and id=#{id,jdbcType=BIGINT}


	and stock_exchange = #{stockExchange,jdbcType=VARCHAR}


	and stock_code = #{stockCode,jdbcType=VARCHAR}


	and create_time = #{createTime,jdbcType=TIMESTAMP}


	and motify_time = #{motifyTime,jdbcType=TIMESTAMP}


	and name = #{name,jdbcType=VARCHAR}

select

from stock
where 1

	and id=#{data.id,jdbcType=BIGINT}


	and stock_exchange =
	#{data.stockExchange,jdbcType=VARCHAR}


	and stock_code = #{data.stockCode,jdbcType=VARCHAR}


	and create_time = #{data.createTime,jdbcType=TIMESTAMP}


	and motify_time = #{data.motifyTime,jdbcType=TIMESTAMP}


	and name = #{data.name,jdbcType=VARCHAR}

limit #{pageNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
